mapper.select:
SELECT M.EMP_CODE, E.NAME EMPNAME, E.DEPTNAME, E.TEAMNAME, E.POSNAME
FROM MESSENGER_VIEW M,
     EMPLOYEE_VIEW E
WHERE M.SEQ = #{seq}
  AND M.EMP_CODE !=#{code}
  AND M.EMP_CODE=E.CODE

mapper.insert:

    SELECT MESSENGER_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO MESSENGER (
SEQ
,TYPE
,NAME
) VALUES (
#{seq}
,#{type}
,#{name}
)

mapper.update:
UPDATE MESSENGER
SET TYPE = 'M'
WHERE SEQ = #{seq}

UPDATE MESSENGER
SET NAME = #{name}
WHERE SEQ = #{seq}
